import urlJoin from "url-join";
import { httpPostSimple } from "./makeRequest";
/**
 * Asks the server to fetch the pipeline definition corresponding to the
 * given pipeline ID. Requires cookies, as this is part of generic issuance
 * user authentication.
 */
export async function requestGenericIssuanceUpsertPipeline(zupassServerUrl, req) {
    return httpPostSimple(urlJoin(zupassServerUrl, `/generic-issuance/api/upsert-pipeline`), async (resText) => ({
        value: JSON.parse(resText),
        success: true
    }), req, true);
}
